    <>
    <p className='comment_body'>{commentBody}</p>
    <p className='usercommented'> - {userCommented} commented</p>
    <p className='EditDel_DisplayComment'>
        <i>Edit</i>
        <i>Delete</i>
    </p>
    </>
  )
}

export default DisplayComments
