import React, { useState } from 'react'
import './CreateEditChannel.css'

function CreateEditChannel({setEditCreateChannelBtn}) {
    const CurrentUser = {
        result: {
            email: 'xyz@gmail.com',
            joinedOn: '2222-07-IST09:57:23.4892',
        },
    };
    const[name, setNam]=useState(CurrentUser?.result.name)
    const[]=useState(CurrentUser?.result.desc)
  return (
    <div className='container_CreateEditChannel'>
        <input type="submit" name='text' value={"X"} className='ibtn_x' onClick={()=>setEditCreateChannelBtn(false)}/>
        <div className='container2_CreateEditChannel'>
            <h1>{
                CurrentUser.result.name ?<>
                Edit
                </>:
                <>Create</>
                }
                 Your Channel</h1>
                 <input type="text" name='text' placeholder='Enter Your/Channel name' className='ibox' />
                 <textarea type='text' rows={15} placeholder={"Enter channel description"} className={'ibox'}/>
                 <input type="submit" value={"submit"} className='ibtn' />
      
      </div>
      
    </div>
  )
}

export default CreateEditChannel
